import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { Link, useNavigate } from 'react-router-dom';


function Login() {
    const loggedInUser = useSelector(state => state.authenticationReducer.loggedInUser)
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");


    const doLogin = () => {
        const payload = {
            email: username,
            password: password

        }
        dispatch(doUserLogin(payload));
    }

    return (
        <div className="container">
            {
                loggedInUser !== null ?
                    navigate(-1)
                    :
                    <div>

                        <div className="form-group">
                            <label htmlFor='username'>Username:</label>
                            <input type="email" className="form-control" placeholder="Enter your username/email" name="username" value={username} onChange={e => setUsername(e.target.value)} />

                        </div>
                        <div className="form-group">
                            <label htmlFor='password'>Password:</label>
                            <input type="password" className="form-control" name="password" placeholder="Enter your Password" value={password} onChange={e => setPassword(e.target.value)} />

                        </div>


                        <div>
                            <button type="submit" onClick={doLogin} class="btn btn-outline-success">Login</button>

                            <button type="submit" class="btn btn-outline-danger">Forgot Password</button>
                        </div>
                        
                    </div>
            }
        </div>
    )
}

export default Login;
