import React, { useState } from 'react';
import Axios from 'axios';
import { Link } from 'react-router-dom';


function Login() {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const doLogin = () => {
        alert("login submitted");
    }

    return (
        <div className="container">

            <div className="form-group">
                <label htmlFor='username'>Username:</label>
                <input type="email" className="form-control" placeholder="Enter your username/email" name="username" value={username} onChange={e => setUsername(e.target.value)} />

            </div>
            <div className="form-group">
                <label htmlFor='password'>Password:</label>
                <input type="password" className="form-control" name="password" placeholder="Enter your Password" value={password} onChange={e => setPassword(e.target.value)} />

            </div>


            <div>
                <button type="submit" class="btn btn-outline-warning"><Link to="/">Guest user</Link></button>

                <button type="submit" class="btn btn-outline-success"><Link to="/user/addUser"> New User</Link></button>

                <button type="submit" onClick={doLogin} class="btn btn-outline-success">Login</button>

                <button type="submit" class="btn btn-outline-danger">Forgot Password</button>



            </div>
            <div>
            <button type="submit" class="btn btn-outline-secondary"><Link to="/"> Back </Link></button>
            </div>
        </div>
    )
}

export default Login;
