import { USER_ADD, USER_FETCH_ALL, USER_FETCH_BYID } from "../actions/ActionConstants";

const initialState = {
    users: [],
    user: null,
    newUser: null,
    updateUser: null,
    deleteUser: null,
    bookingUser: null,
    carUser: null
}
export default function userReducers(state = initialState, action) {

    if (action.type === USER_FETCH_ALL) {
        console.log(action.payload);
        return ({
            ...state,
            users: action.payload
        })
    }

    if (action.type === USER_FETCH_BYID) {
        console.log(action.payload);
        return ({
            ...state,
            user: action.payload
        })
    }

    if (action.type === 'USER/FETCHBOOKING') {
        console.log(action.payload);
        return ({
            ...state,
            bookingUser: action.payload
        })
    }

    if (action.type === 'USER/UPDATESUCCESS') {
        console.log(action.payload);
        return ({
            ...state,
            updateUser: action.payload
        })
    }

    if (action.type === USER_ADD) {
        console.log(action.payload);
        return ({
            ...state,
            addUser: action.payload
        })
    }

    if (action.type === 'USER/DELETESUCCESS') {
        console.log(action.payload);
        return ({
            ...state,
            deleteUser: action.payload
        })
    }

    else {
        return state;
    }
}
