import React, { useEffect, useState } from "react";

import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { deleteUser, getUserBookings, getUserDetails } from "../store/actions/UserAction";
import image2 from '../images/image2.jpg';

import NavBarUpdateDelete from "../navbars/NavBarUpdateDelete";



function FetchBookingByUserId() {

    const user = useSelector(state => state.userReducer.user)
    const bookingUser=useSelector(state => state.userReducer.bookingUser)
    const carUser=useSelector(state => state.userReducer.carUser)

    const { id } = useParams();
    const dispatch = useDispatch();



    const fetchUserById = () => {
        dispatch(getUserBookings(id));


    }
    useEffect(fetchUserById, [id]);


    return (
        <div className='container-booking'>
            <NavBarUpdateDelete />

            {
                user !== null &&
                <div className="row">
                    <div className="col">
                        <img src={image2} alt="card image" />
                    </div>
                    <div className="col">
                        
                        <h3>user Details</h3>
                        <p>userID:{bookingUser.userId}</p>
                        <p>Name:{bookingUser.name}</p>
                        <p>Address:{bookingUser.address}</p>
                        <p>phone:{bookingUser.phone}</p>
                        <p>email:{bookingUser.email}</p>                       
                        <p>password:{bookingUser.password}</p>

                        <p>user booking details</p>
                        <p>BookingId:{bookingUser.bookingId}</p>
                        <p>Booking date:{bookingUser.bookingDate}</p>
                        <p>startTime:{bookingUser.startTime}</p>
                        <p>Endtime:{bookingUser.endTime}</p>
                        <p>BookingAMnt:{bookingUser.bookingAmount}</p>

                        <p>cardetails</p>
                        <p>carId:{bookingUser.carId}</p>
                        <p>car number:{bookingUser.carNumber}</p>
                        <p>carModel:{bookingUser.carModel}</p>
                        <p>seatingCapacity:{bookingUser.seatingCapacity}</p>
                        <p>rentalPrice:{bookingUser.rentalPrice}</p>
                        <p>fuelType:{bookingUser.fuelType}</p>

                        <Link to={`/user/update/${user.userId}`} className="btn btn-secondary">Update</Link> &nbsp;&nbsp;
                        <Link to={`/user/delete/${user.userId}`} className="btn btn-secondary">Delete this account</Link>
                        
                        
                    </div>

                </div>
            }
            <div>
                <Link to="/user/all" className="btn btn-secondary">Back to List</Link>

            </div>
        </div>
    )

}
export default FetchBookingByUserId;
