import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { forgetPassword } from "../store/actions/ForgotPasswordAction";

function ForgetPassword() {
    //const loggedInUser = useSelector(state => state.authenticationReducer.loggedInUser)
    const fgPassword = useSelector(state => state.ForgetPassword)
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [password,setPassword]=useState("");

    const onChange = async (e) => {
        const payload = {
            password:password

        }
        dispatch(forgetPassword(payload));
        alert("Password changed Sucessfully! ");
        navigate(-1);
    }



    return (
        <div className="container">



            <header>
                <h3>ForgetPassword</h3>
            </header>


            <div className="form-group">
                <label htmlFor='email'>Email:</label>
                <input type="email" className="form-control" placeholder="Enter your Email" name="email" value={email} onChange={e => setEmail(e.target.value)} />

            </div>


            <div className="form-group">
                <label htmlFor='phone'>Phone:</label>
                <input type='phone' className="form-control" placeholder="Enter Phone number" id="phone" value={phone} onChange={e => setPhone(e.target.value)} />

            </div>

            <div className="form-group">
                <label htmlFor='phone'>Enter New Password</label>
                <input type='text' className="form-control" placeholder="Enter New Password" id="newpassword" value={newpassword}  />

            </div>

            <div>

                <button onClick={onChange} class="btn btn-outline-success">Change Password</button>
            </div>




        </div >
    )
}
export default ForgetPassword;
