import axios from 'axios';



export function getAllUser() {


    // instead of returning action,it return another function(FirstClass functions)
    //
    return (dispatch) => {

        return axios.get("http://localhost:8084/user/all").then(
            resp => {
                dispatch(getAllUserSuccess(resp.data));


            }
        )
    }
}
export function getAllUserSuccess(data) {
    return {
        type: "USER/FETCHALL",
        payload: data
    }
}
export function getUserDetails(id) {
    return (dispatch) => {
        return axios.get("http://localhost:8084/user/" + id).then(resp => {
            console.log(resp.status);
            dispatch(getUserByIdSuccess(resp.data))

        })
    }
}
export function getUserByIdSuccess(data) {
    return {
        type: "USER/FETCHBYID",
        payload: data
    }
}

export function updateUser(user) {
    return (dispatch) => {
        return axios.put("http://localhost:8084/user/update", user).then(resp => {
            dispatch(updateUserSuccess(resp.data));
        }
        )
    }
}

export function updateUserSuccess(data) {
    return {
        type: 'USER/UPDATESUCCESS',
        payload: data
    }
}

export function addUser(user) {
    return (dispatch) => {
        return axios.post("http://localhost:8084/user/addUser", user).then(resp => {
            dispatch(addUserSuccess(resp.data));
        })
        // alert("User saved with id:" + resp.data.userId));
    }
}
export function addUserSuccess(data) {
    return {
        type: 'USER/ADDSUCCESS',
        payload: data
    }
}

export function deleteUser(id){
    return(dispatch)=>{
        return axios.delete("http://localhost:8084/user/delete/"+id).then(resp =>{
            console.log(resp.status);
            dispatch(deleteUserSuccess(resp.data));
            //alert("User deleted");
        })
    }
}
export function deleteUserSuccess(data){
    return{
        type:'USER/DELETESUCCESS',        
        payload:data
    }
}
