import { useEffect } from "react";
import NavBarUpdateDelete from "../../navbars/NavBarUpdateDelete";
import { deleteUser } from "../../store/actions/UserAction";
import userdelete from '../../images/userdelete.jpg';
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate, useParams } from "react-router-dom";

    function DeleteUser() {

        const user = useSelector(state => state.userReducer.user)

        const { id } = useParams();

        const dispatch = useDispatch();

        const navigate = useNavigate();



        const deleteHandler = () => {

            dispatch(deleteUser(id));

        }
        useEffect(deleteHandler, []);
        dispatch(deleteUser(user.id));   


        return (

            <div className='container-fluid'>

                <NavBarUpdateDelete />

                {

                    user !== null &&

                    <div className="row">

                        <div className="col">
                            <img src={userdelete} alt="card image" />

                        </div>
                        <div className="col">
                            {/* <p>userID:{user.userId}</p> */}
                            {/* <h3>Are you sure ? You want to delete your Account permanently</h3>                           */}

                            {/* <button className="btn btn-secondary" onClick={deleteHandler}>Yes delete</button>&nbsp; */}

                            <Link to={`#`} onClick={() => {if(window.confirm('Are you sure to delete this record?'))
                            { deleteHandler(user.id)};}}> <i className="material-icons">Delete</i> </Link>

                            {/* <button onClick={() => navigate(-1)} className="btn btn-secondary">No</button> */}

                           
    
    {/*                         <p>Name:{user.name}</p>
    
                            <p>Address:{user.address}</p>
    
                            <p>phone:{user.phone}</p>
    
                            <p>email:{user.email}</p>
    
                            <p>password:{user.password}</p>  */}



                        </div>
                    </div>

                }



                {/* <div>
    
                    <button className="btn btn-secondary" onClick={deleteUser}>Yes delete</button>
    
    
    
                    <button onClick={() => navigate(-1)} className="btn btn-secondary">No</button>
    
                </div> */}


            </div >





        )

    }

export default DeleteUser;
