import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { forgetPassword } from "../store/actions/ForgotPasswordAction";
import NavBarRHome from "../navbars/NavBarHome";

function ForgetPassword() {
    //const loggedInUser = useSelector(state => state.authenticationReducer.loggedInUser)
    const fgPassword = useSelector(state => state.ForgetPassword)
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [uEmail, setUEmail] = useState("");
    const [uPhone, setUPhone] = useState("");
    const [uPassword, setUPassword] = useState("");

    const onChange = async (e) => {
        const payload = {
            password: uPassword

        }
        dispatch(forgetPassword(payload));
        alert("Password changed Sucessfully! ");
        navigate(-1);
    }



    return (
        <div className="container">
            <NavBarRHome />



            <header>
                <h3>ForgetPassword</h3>
            </header>


            <div className="form-group">
                <label htmlFor='email'>Email:</label>
                <input type="email" className="form-control" placeholder="Enter your Email" name="uEmail" value={uEmail} onChange={e => setUEmail(e.target.value)} />

            </div>


            <div className="form-group">
                <label htmlFor='phone'>Phone:</label>
                <input type='phone' className="form-control" placeholder="Enter Phone number" id="uPhone" value={uPhone} onChange={e => setUPhone(e.target.value)} />

            </div>

            <div className="form-group">
                <label htmlFor='phone'> Password:</label>
                    <input type="password" className="form-control" placeholder="Enter Password" name="uPassword" value={uPassword} onChange={e => setUPassword(e.target.value)}/>
                
            </div>

            <div>

                <button onClick={onChange} class="btn btn-outline-success">Change Password</button>
            </div>




        </div >
    )
}
export default ForgetPassword;
