import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { deleteUser, getUserDetails } from "../store/actions/UserAction";

function DeleteUser() {
    const user = useSelector(state => state.userReducer.user)
    const { id } = useParams();
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const fetchUserById = () => {
        dispatch(deleteUser(id));
    }
    useEffect(fetchUserById, []);
    

    dispatch(deleteUser(user.id));   
    // navigate('/');
    alert("Deleted Account cannot be restored. Do you wish to continue ?");



    return (
        <div>
            {
                user !== null &&
                // <div className="row">
                //     <div className="col">
                //         <img src={image2} alt="card image" />
                //     </div>
                <div className="col">
                    <h3>Ready to delete</h3>
                    <p>userID:{user.userId}</p>
                    <p>Name:{user.name}</p>
                    {/* <p>Address:{user.address}</p>
                    <p>phone:{user.phone}</p>
                    <p>email:{user.email}</p>
                    <p>password:{user.password}</p> */}

                </div>
            }
            <div>
            <button className="btn btn-secondary" onClick={deleteUser}>Delete this account</button> 
           
                    <button onClick={() => navigate(-1)} className="btn btn-secondary">Back</button>
                </div>          
        </div >
    )
}
export default DeleteUser;
