import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate, useParams } from "react-router-dom";
import { deleteUser, getUserDetails } from "../store/actions/UserAction";
import acdelete from '../images/acdelete.jpg';
import NavBar from "./NavBar";

function DeleteUser() {
    const user = useSelector(state => state.userReducer.user)
    const { id } = useParams();
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const fetchUserById = () => {
        dispatch(deleteUser(id));
    }
    useEffect(fetchUserById, []);


    dispatch(deleteUser());
    // navigate('/');
    alert("Deleted Account cannot be restored. Do you wish to continue ?");
    navigate(-1);



    return (
        <div className='container-fluid'>
            <NavBar />
            {
                user !== null &&
                <div className="row">
                    <div className="col">
                        <img src={acdelete} alt="card image" />
                    </div>

                    <div className="col">

                        <h3>Are you sure ? You want to delete your Account permanently</h3>
                        <button className="btn btn-secondary" onClick={deleteUser}>Yes delete</button>&nbsp;

                        <button className="btn btn-secondary"><Link to={`/user/delete/${user.userId}`}>No</Link></button>
                        {/* <p>userID:{user.userId}</p>
                        <p>Name:{user.name}</p>
                        <p>Address:{user.address}</p>
                        <p>phone:{user.phone}</p>
                        <p>email:{user.email}</p>
                        <p>password:{user.password}</p> */}

                    </div>
                </div>
            }

            {/* <div>
                <button className="btn btn-secondary" onClick={deleteUser}>Yes delete</button>

                <button onClick={() => navigate(-1)} className="btn btn-secondary">No</button>
            </div> */}

        </div >



    )
}
export default DeleteUser;
