import React, { useEffect } from 'react';
// import Axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getAllUser } from '../store/actions/UserAction';
import pic2 from '../images/pic2.jpg';

function FetchAllUsers() {


    const user = useSelector(state => state.userReducer.users)
    const dispatch = useDispatch();

    const myFunction = async () => {
        dispatch(getAllUser())

    };
    useEffect(() => { myFunction() }, []);


    return (

        <div>
            <div class="row">
                {
                    user.map((p, index) =>
                        <div key={index} className="col-sm-3">
                            <Link to={`/user/get/${p.userId}`}>
                                <div className="card" style={{ width: "200px", height: "400px" }}>
                                <img className="card-img-top" src={pic2} alt="Card image" />
                                    <div className='card-body'>
                                        <h4 className='car-title'>{p.name}</h4>
                                        <p className='card-text'>{p.phone}</p>

                                    </div>
                                </div>
                            </Link>
                        </div>
                    )
                }
            </div>
        </div>


    )
}
export default FetchAllUsers;
