import React, { useEffect, useState } from "react";

import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { deleteUser, getUserDetails } from "../store/actions/UserAction";
import image2 from '../images/image2.jpg';
import NavBar from "./NavBar";



function FetchUser() {

    const user = useSelector(state => state.userReducer.user)

    const { id } = useParams();
    const dispatch = useDispatch();



    const fetchUserById = () => {
        dispatch(getUserDetails(id));


    }
    useEffect(fetchUserById, [id]);


    return (
        <div className='container-fluid'>
            <NavBar />

            {
                user !== null &&
                <div className="row">
                    <div className="col">
                        <img src={image2} alt="card image" />
                    </div>
                    <div className="col">
                        <h3>user Details</h3>
                        <p>userID:{user.userId}</p>
                        <p>Name:{user.name}</p>
                        <p>Address:{user.address}</p>
                        <p>phone:{user.phone}</p>
                        <p>email:{user.email}</p>
                        <p>password:{user.password}</p>
                        <Link to={`/user/update/${user.userId}`} className="btn btn-secondary">Update</Link> &nbsp;&nbsp;
                        <Link to={`/user/delete/${user.userId}`}>  className="btn btn-secondary">Delete this account</Link>
                        
                        
                    </div>

                </div>
            }
            <div>
                <Link to="/user/all" className="btn btn-secondary">Back to List</Link>

            </div>
        </div>
    )

}
export default FetchUser;
