import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { forgetPassword } from "../store/actions/ForgotPasswordAction";

function ForgetPassword() {
    //const loggedInUser = useSelector(state => state.authenticationReducer.loggedInUser)
    const fgPassword = useSelector(state => state.ForgetPassword)
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");

    const onChange = async (e) => {
        const payload = {
            email: email,
            phone: phone

        }
        dispatch(forgetPassword(payload));
    }



    return (
        <div className="container">
            <header>
                <h3>ForgetPassword</h3>
            </header>
            <main>
                <form onSubmit={onSubmit}>
                    <input type='email' className="email" placeholder="Enter your Email" id="email" value={email} onChange={e => setEmail(e.target.value)} />
                    <input type='phone' className="phone" placeholder="Enter Phone number" id="phone" value={phone} onChange={e => setPhone(e.target.value)} />
                    <div>
                            {/* <button onClick={doSignIn} class="btn btn-outline-success">Login</button>&nbsp; */}

                            <button onClick={onChange} class="btn btn-outline-danger">Forgot Password</button>
                        </div>

                </form>
            </main>

        </div>
    )
}
export default ForgetPassword;
