import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate, useParams } from "react-router-dom";
import { addUser } from "../store/actions/UserAction";



function AddUser() {

    const user = useSelector(state => state.userReducer.user);
    const navigate = useNavigate();
    const dispatch = useDispatch();
    


    const [uId, setUId] = useState();
    const [uName, setUName] = useState("");
    const [uAddress, setUAddress] = useState("");
    const [uPhone, setUPhone] = useState("");
    const [uEmail, setUEmail] = useState("");
    const [uPassword, setUPassword] = useState("");


    const handleSubmit = () => {
        const payload = {
            userId: uId,
            name: uName,
            address: uAddress,
            phone: uPhone,
            email: uEmail,
            password: uPassword


        }
        dispatch(addUser(payload));
        alert("User Added successfully with :" + `${user.userId}`);
        navigate(-1);


    }
    return (
        <div className="container">
            <form>

                <h2 style={{ fontStyle: "italic" }}>Register user</h2>

                <div >

                    <label htmlFor="uName">UserName:
                        <input type="text" className="form-control" name="uName" placeholder="Enter your Name" value={uName} onChange={e => setUName(e.target.value)} />
                    </label>
                </div>
                <div>
                    <label htmlFor="uAddress">Address:
                        <input type="text" className="form-control" name="uAddress" placeholder="Enter your Address" value={uAddress} onChange={e => setUAddress(e.target.value)} />

                    </label>


                </div>
                <div >
                    <label htmlFor="uPhone"> Phone:
                        <input type="text" className="form-control" name="uPhone" placeholder="Enter your valid PhoneNumber" value={uPhone} onChange={e => setUPhone(e.target.value)} />
                    </label>


                </div>
                <div>
                    <label>
                        email:
                        <input type="email" className="form-control" name="uEmail" placeholder="Enter your Email" value={uEmail} onChange={e => setUEmail(e.target.value)} />                </label>
                </div>
                <div>
                    <label>
                        Password:
                        <input type="password" className="form-control" placeholder="Enter Password" name="uPassword" value={uPassword} onChange={e => setUPassword(e.target.value)} />                </label>
                </div>
                {/* <button onClick={handleSubmit} className="btn btn-primary">Submit</button> */}

                <div>
                    <button onClick={handleSubmit} className="btn btn-primary">Submit</button>&nbsp;
                    <button type="button" class="btn btn-secondary"><Link to="/">Back</Link></button>
                    {/* <button onClick={() => navigate(-1)} className="btn btn-secondary">Back</button> */}

                </div>
            </form>


        </div>


    )
}
export default AddUser;
