import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getAllCars } from "../store/actions/CarAction";
import NavBar from "./NavBar";
import car5 from '../images/car5.webp';

function FetchAllCars() {


    const car = useSelector(state => state.carReducer.cars)
    const dispatch = useDispatch();

    const myFunction = async () => {
        dispatch(getAllCars())

    };
    useEffect(() => { myFunction() }, []);


    return (

        <div>
           <NavBar/>
            <div class="row">
                {
                    car.map((p, index) =>
                        <div key={index} className="col-sm-3">
                            <Link to={`/car/get/${p.carId}`}>
                                <div className="card" style={{ width: "250px", height: "300px" }}>
                                <img className="card-img-top" src={car5} alt="Card image" />
                                    <div className='card-body'>
                                        <h4 className='car-title'>{p.model}</h4>
                                        <p className='card-text'>{p.carId}</p>

                                    </div>
                                </div>
                            </Link>
                        </div>
                    )
                }
            </div>
        </div>


    )
}
export default FetchAllCars;
