const initialState={
    loggedInUser:null

}
export default function authenticationReducers(state = initialState, action) {

    if (action.type === 'LOGIN/SUCCESS') {
        console.log(action.payload);
        return ({
            ...state,
            loggedInUser: action.payload
        })
    }
    else {
        return state;
    }
}
