const initialState = {
    users: [],
    user:null
}
export default function userReducers(state = initialState, action) {

    if (action.type === 'USER/FETCHALL') {
        console.log(action.payload);
        return ({
            ...state,
            users: action.payload
        })
    }
    if(action.type==='USER/FETCHBYID'){
        console.log(action.payload);
        return({
            ...state,
            user:action.payload
        })
    }

    else {
        return state;
    }
}
